figure.artwork:
software-identity = { global-attr, * content: [ entity / evidence / link / software-meta / payload / any-element], ; review for interoperability ? corpus: bool, ? patch: bool, ? media: text, name: text, ? supplemental: bool, tagId: text, ? tag-version: integer, ? version: text, ? version-scheme: NMTOKEN, }
NMTOKEN = text ; .regexp to add some validation? NMTOKENS = [* NMTOKEN]
any-attr = text any-element = any
date-time = time any-uri = uri
global-attr = ( * (text => any-attr), ? lang: text, )
meta-type = ( global-attr, * (text => any-attr), )
meta-element = [ global-attr, * (text => any-attr), ]
resource-collection = ( global-attr, * (directory: directory // file: file // process: process // resource: resource) )
file = { filesystem-item, ? size: integer, ? version: text, * (text => any-attr), }
filesystem-item = ( meta-type, ? key: bool, ? location: text, name: text, ? root: text )
directory = { filesystem-item, path-elements: [* (directory / file)], }
process = { global-attr, name: text, ? pid: integer, }
resource = { global-attr, type: text, }
entity = { global-attr, meta-elements: [* meta-element], name: text, ? regid: any-uri, role: NMTOKENS, ? thumbprint: text, }
evidence = { global-attr, resource-collection, ? date: date-time, ? device-id: text, }
link = { global-attr, ? artifact: text, href: any-uri, ? media: text, ? ownership: ("shared" / "private" / "abandon"), rel: NMTOKEN, ? type: text, ? use: ("optional" / "required" / "recommended"), }
software-meta = { global-attr, ? activation-status: text, ? channel-type: text, ? colloquial-version: text, ? description: text, ? edition: text, ? entitlement-data-required: bool, ? entitlement-key: text, ? generator: text, ? persistent-id: text, ? product: text, ? product-family: text, ? revision: text, ? summary: text, ? unspsc-code: text, ? unspsc-version: text, }
payload = { global-attr resource-collection, }
